package controlStatements;

public class ElseIfStudy6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String city="go";
		if (city=="pune") {
			System.out.println("city is pune");
		}
		
		else if (city=="goa") {
			System.out.println("city is goa");
		}
		
		else if (city=="delhi") {
			System.out.println("city is delhi");
		}
		else if (city=="satara") {
			System.out.println("city is satara");
		}
		else {
			System.out.println("city is.......");
		}
	}

}
